import React from 'react';
import {Button, TextField } from '@material-ui/core';

import './habits.css'
import AddHabit from '../addHabit';


class Item extends React.Component {
  constructor (props){
    super ();

    this.state = {
      checked: false
    };

    this.handleClick = this.handleClick.bind(this);    
  }
  handleClick (e){
    this.setState({
      checked: !this.state.checked
    });

  }
  render (){

    let text = this.state.checked ? <strike>{this.props.message}</strike> : this.props.message;
    return (
        <div className="row">
          <div className="col-md-12">
            <input type="checkbox" onClick={this.handleClick} />&nbsp;{text}
            <hr />
          </div>
        </div>
    );
  }
}


export default class HabitsList extends React.Component {

  constructor (props){
    super ();
    this.state = {
      habitslist: [
          {
            id: 1,
            title: "Drink more water"
          },
          {
            id: 2,
            title: "No coffee"
          },
          {
            id: 3,
            title: "Morning workout"
          },
          {
            id: 4,
            title: "More time outside"
          },
      ]

    };

  }

  updateItem(id, itemAttributes) {
    var index = this.state.habitslist.findIndex(x=> x.id === id);

      this.setState({
        habitslist: [
           ...this.state.habitslist.slice(0,index),
           Object.assign({}, this.state.habitslist[index], itemAttributes),
           ...this.state.habitslist.slice(index - 1)
        ]
      });
  }

  addNewHabit(str){
    console.log(this.state.habitslist.length)
    this.updateItem(this.state.habitslist.length + 1, {title: str});
    // this.setState(state =>{
    //   const habitslist = state.habitslist.push(str)
    // });

  }

  // onAddItem = () => {
  //   // not allowed AND not working
  //   this.setState(state => {
  //     const list = state.list.push(state.value);

  //     return {
  //       list,
  //       value: '',
  //     };
  //   });
  // };


  render (){
    let allTheThings = [];
    for (var i = 0; i < this.state.habitslist.length; i++) { 
        allTheThings.push(<Item message={this.state.habitslist[i].title} />);
      }
    // let allTheThings = [<Item message={this.props.mes1} />, <Item message={this.props.mes2} />, 
    //   <Item message={this.state.mes1} />, <Item message={this.props.mes4} />]
    let items = allTheThings.map(thing => thing);

    return (

      <div className='habits-list'>

        <h2>Habits List</h2>

        <h4>{items}</h4>
        {/* <TextField id="standard-secondary" label="Enter new habit" color="secondary" />
        <Button className='button1' variant="contained" color="primary">
          Add habit
        </Button> */}
        <AddHabit  onAdd={(e) => this.addNewHabit(e)} ></AddHabit>




      </div>
    );
  }
}
