    }

    let progress = 0;

    function simulateLoading() {
      setTimeout(() => {
        const progressBefore = progress;
        progress += Math.random() * 20;
        app.progressbar.set(progressBarEl, progress);

        if (progressBefore < 100) {
          simulateLoading(); // keep "loading"
