class HabitProgress extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      percentage: 0
    }
    this.nextStep = this.nextStep.bind(this)
  }
  nextStep() {
    if(this.props.percentage === 100) return 
    this.setState(prevState => ({ percentage: prevState.percentage + 12.5 }))
  }

  render() {
    return (
      <div className = 'progress'>

        <h2>{this.props.title}</h2>
        <ProgressBar percentage={this.props.percentage} />

        <div style={{ marginTop: '20px' }}>  

          <button
            onClick={this.nextStep}
           >
            {this.props.increment}
          </button>  

          <button style={{width: '70px', height:'30px', marginTop: '10px',backgroundColor: 'rgba(231, 27, 27, 0.795)', marginBottom: '15px'}} onClick={() => this.setState({ percentage: 0 })}> {this.props.reset}</button>

        </div>   

      </div>
    )
  }  
}

const ProgressBar = (props) => {
  return (
      <div className="progress-bar">
        <Filler percentage={this.props.percentage} />
      </div>
    )
}

const Filler = (props) => {
  return <div className="filler" style={{ width: `${props.percentage}%` }} />
}


