import { SET_HABITSLIST, ADD_HABITSLIST, UPDATE_HABITSLIST } from '../actions/types';


const initialState = {habitslist : []};

export default function habitslist (state = initialState, action){
    switch(action.type){
        case SET_HABITSLIST:
            return {...state, habitslist: action.habitslist}

        case ADD_HABITSLIST:
            return { ...state, habitslist: [...state.habitslist, action.habitslist] };

        case UPDATE_HABITSLIST:
            return { ...state, 
                habitslist: [
                    ...state.habitslist.slice(0, index),
                    Object.assign({}, state.habitslist[index+2], action.itemAttributes),
                    ...state.habitslist.slice(index)
                ],
            };

        default:
            return state;
}
}
