export default class HabitProgress extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      habits: [
        {
          title: "Water tracker",
          percentage: 0,
          increment: "+1 glass",
          reset: "Reset"
        },
        {
          title: "Else ",
          percentage: 0,
          increment: "+",
          reset: "Reset"
        },
      ]

    }

    this.nextStep = this.nextStep.bind(this)
  }

  nextStep() {
    if(this.state.percentage === 100) return 
    this.setState(prevState => ({ percentage: prevState.percentage + 12.5 }))
  }

  render() {
    const { list } = this.props;
    return (
      <div className = 'progress'>

        <h2>{this.state.title}</h2>
        <ProgressBar percentage={list.percentage} />

        <div style={{ marginTop: '20px' }}>  

          <button
            onClick={this.nextStep}
           >
            {list.increment}
          </button>  

          <button style={{width: '70px', height:'30px', marginTop: '10px',backgroundColor: 'rgba(231, 27, 27, 0.795)', marginBottom: '15px'}} onClick={() => this.setState({ percentage: 0 })}> {list.reset}</button>

        </div>   

      </div>
    )
  }  
}

const ProgressBar = (props) => {
  return (
      <div className="progress-bar">
        <Filler percentage={props.percentage} />
      </div>
    )
}

const Filler = (props) => {
  return <div className="filler" style={{ width: `${props.percentage}%` }} />
}
